class RandomClassifier: # CamelCase âœ…

    def fit(self, X, y):
        self.classes_ = np.unique(y)

    def predict(self, X):
        return np.random.choice(self.classes_, size=len(X))

    def fit_predict(self, X, y): # lowercase âœ…
        self.fit(X, y)
