Extract method
def error(scale):
    return (random.random() - 0.5) / scale)

def linear_model(x,*,a=0.0,b=1.0):
    return a+b*x

def clip(x, *, min_value=0.0):
    return min(min_value,x)

def predict(x): 
    draft = linear_model(x, a=0.5, b=2.0) + error(scale=10.0)
    return  clip(draft, min_value=0.)

